package sliding_window.lc_05_76_Minimum_Window_Substring;

import java.util.HashMap;
import java.util.Map;

public class Solution2 {
    public String minWindow(String s, String t) {
        if (t.length() > s.length()) {
            return "";
        }
        char[] tChars = t.toCharArray();
        if (!arraysEqualsIgnoreOrder(s.toCharArray(), tChars)) {
            return "";
        }
        int size = t.length() - 1;
        long count = 0;
        while (size < s.length()) {
            size++;
            int left = 0;
            int right = size;
            while (right <= s.length()) {
                count++;
                if (!arraysEqualsIgnoreOrder(s.substring(left, right).toCharArray(), tChars)) {
                    left++;
                    right++;
                } else {
                    System.out.println(count);
                    return s.substring(left, right);
                }
            }
        }
        return "";
    }

    private boolean arraysEqualsIgnoreOrder(char[] charsS1, char[] charsS2) {
        Map<Character, Integer> map = new HashMap<>();
        for (char c : charsS1) {
            map.put(c, map.getOrDefault(c, 0) + 1);
        }
        for (char c : charsS2) {
            Integer integer = map.get(c);
            if (integer != null) {
                map.put(c, integer - 1);
                if (integer - 1 < 0) {
                    return false;
                }
            } else {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        Solution2 solution = new Solution2();
        System.out.println(solution.minWindow("ADOBECODEBANC", "ABC"));
        System.out.println(solution.minWindow("a", "a"));
        System.out.println(solution.minWindow("a", "aa"));
        System.out.println(solution.minWindow("a", "b"));
        System.out.println(solution.minWindow("ab", "a"));
        System.out.println(solution.minWindow("cabwefgewcwaefgcf", "cae"));
        System.out.println(solution.minWindow("", ""));
    }
}
